      //   // Update the appropriate state
      //   const { name, value } = event.target;
      //   this.setState({
      //     [name]: value
      //   });
      // };


render() {
    return(
        <div className = "container">
        <h1 className="centered">Your Saved Books</h1>
          <section>
          {this.state.books.map(book => {
                    return (
                      <div>
                      key={book.title}
                      title={book.title}
                      author={book.author}
                      href={book.about}
                      thumbnail={book.thumbnail}
                      value={book}
                      id={book._id}
                      </div>
                    );
                  })}
          </section>
        </div>
    )
}

}




export default Saved;
